@Name("hens")
@Scope(APPLICATION) 
public class HenHouse
{
    Set<Hen> hens;

    @In(required=false) Hen hen;

    @Unwrap
    public List<Hen> getHens()
    {
        if (hens == null)
        {
            // Setup our hens
        }
        return hens;
    }

    @Observer({"chickBorn", "chickenBoughtAtMarket"})
    public addHen()
    {
        hens.add(hen);
    }

    @Observer("chickenSoldAtMarket")
    public removeHen()
    {
        hens.remove(hen);
    }

    @Observer("foxGetsIn")
    public removeAllHens()
    {
        hens.clear();
    }
    ...
}
